import{_ as i}from"./chunks/theme.CaArFlcR.js";import{_ as l,c as h,o as n,aG as t,G as o,j as e,a as s}from"./chunks/framework.CdnsPyyz.js";const P=JSON.parse('{"title":"金融学相关术语","description":"","frontmatter":{"title":"金融学相关术语","author":"Zack Zheng","date":"2025/04/27 11:20","categories":["金融学"],"tags":["经济学","金融学"]},"headers":[],"relativePath":"blogs/金融学相关术语.md","filePath":"blogs/金融学相关术语.md","lastUpdated":1749026095000}'),d={name:"blogs/金融学相关术语.md"};function p(_,a,c,m,u,b){const r=i;return n(),h("div",null,[a[0]||(a[0]=t('<h4 id="转移净收入" tabindex="-1">转移净收入 <a class="header-anchor" href="#转移净收入" aria-label="Permalink to &quot;转移净收入&quot;">​</a></h4><p>转移净收入 = 转移性收入 - 转移性支出</p><p>转移性收入：比如离退休金、失业救济金、辞退金、保险索赔、住房公积金、养老金、子女给父母的赡养费等</p><p>转移性支出：缴纳税款、养老保险、医疗保险、赡养支出、捐赠支出、个人所得税、捐款</p><hr><h4 id="名义工资" tabindex="-1">名义工资 <a class="header-anchor" href="#名义工资" aria-label="Permalink to &quot;名义工资&quot;">​</a></h4><p>名义工资是指以货币数量表示的工资数额，也被称为货币工资。它是雇主在一定时期内支付给雇员的货币报酬，不考虑物价变动因素对货币购买力的影响。 （就是多少钱工资，名义工资上涨不一定意味这劳动者实际生活水平提高）</p><hr><h4 id="经常账户" tabindex="-1">经常账户 <a class="header-anchor" href="#经常账户" aria-label="Permalink to &quot;经常账户&quot;">​</a></h4>',9)),o(r,{src:"https://gitee.com/zackzhengxy/picGallery/raw/main/imgs/经常账户.png"}),a[1]||(a[1]=e("hr",null,null,-1)),a[2]||(a[2]=e("h4",{id:"债券",tabindex:"-1"},[s("债券 "),e("a",{class:"header-anchor",href:"#债券","aria-label":'Permalink to "债券"'},"​")],-1)),o(r,{src:"https://gitee.com/zackzhengxy/picGallery/raw/main/imgs/债券.png"}),a[3]||(a[3]=t('<hr><h4 id="通货膨胀" tabindex="-1">通货膨胀 <a class="header-anchor" href="#通货膨胀" aria-label="Permalink to &quot;通货膨胀&quot;">​</a></h4><p>是指在货币流通条件下，因货币供给大于货币实际需求，也即现实购买力大于产出供给，导致货币贬值，而引起的一段时间内物价持续而普遍地上涨现象。</p><p>实质是社会总需求大于社会总供给</p><p>(可以理解为大家都有钱了，都想买，但是东西不够了，价格就涨了)</p><hr><h4 id="通货紧缩" tabindex="-1">通货紧缩 <a class="header-anchor" href="#通货紧缩" aria-label="Permalink to &quot;通货紧缩&quot;">​</a></h4><p>指的是市场上流通的货币量少于商品流通中所需要的货币量而引起的货币升值、物价普遍持续下跌的状况</p><p>质是社会总需求小于社会总供给</p><p>(可以理解为大家都没钱了，东西多了，价格就跌了)</p><hr><h4 id="滞胀" tabindex="-1">滞胀 <a class="header-anchor" href="#滞胀" aria-label="Permalink to &quot;滞胀&quot;">​</a></h4><p>停滞性通货膨胀，是指经济停滞、失业及通货膨胀同时持续高涨的经济现象，也就是说在经济衰退或增长缓慢、失业率上升的同时，物价水平却持续上涨</p><hr><h4 id="离岸市场" tabindex="-1">离岸市场 <a class="header-anchor" href="#离岸市场" aria-label="Permalink to &quot;离岸市场&quot;">​</a></h4><p>离岸市场是指在原货币发行国境外进行该国货币存储与借贷等金融交易的市场</p><hr><h4 id="出清" tabindex="-1">出清 <a class="header-anchor" href="#出清" aria-label="Permalink to &quot;出清&quot;">​</a></h4><h5 id="市场出清" tabindex="-1">市场出清 <a class="header-anchor" href="#市场出清" aria-label="Permalink to &quot;市场出清&quot;">​</a></h5><p>指在市场经济中，商品的供给与需求达到平衡状态，不存在过剩或短缺。当市场价格调整到使供给量等于需求量时，市场实现出清。比如在房地产市场中，当房屋的供给数量和人们对房屋的需求数量相等时，房地产市场达到出清状态，此时房地产的价格是均衡价格。</p><h5 id="债务出清" tabindex="-1">债务出清 <a class="header-anchor" href="#债务出清" aria-label="Permalink to &quot;债务出清&quot;">​</a></h5><p>在经济活动中，企业或个人通过各种方式偿还债务，降低债务水平。例如，一些企业可能通过出售资产、增加盈利等方式来偿还债务，实现债务出清。</p><hr><h4 id="宏观杠杆" tabindex="-1">宏观杠杆 <a class="header-anchor" href="#宏观杠杆" aria-label="Permalink to &quot;宏观杠杆&quot;">​</a></h4><p>宏观杠杆率是指一个国家或地区整体的债务水平，具体是指全社会债务余额与国内生产总值（GDP）的比值，它衡量了经济总体的债务负担情况</p><hr>',26))])}const x=l(d,[["render",p]]);export{P as __pageData,x as default};
