import{_ as o}from"./chunks/theme.CzSWMr6K.js";import{_ as l,c as n,o as i,aG as s,G as r,j as e,a as h}from"./chunks/framework.CdnsPyyz.js";const x=JSON.parse('{"title":"零零碎碎的知识点","description":"","frontmatter":{"title":"零零碎碎的知识点","lang":"en-US","date":"2025/04/22 16:08:00","editLink":true,"categories":["博客"],"tags":["知识点"]},"headers":[],"relativePath":"blogs/零零碎碎的知识点.md","filePath":"blogs/零零碎碎的知识点.md","lastUpdated":1746606972000}'),d={name:"blogs/零零碎碎的知识点.md"};function p(_,a,c,m,u,g){const t=o;return i(),n("div",null,[a[0]||(a[0]=s('<h5 id="总和生育率" tabindex="-1">总和生育率： <a class="header-anchor" href="#总和生育率" aria-label="Permalink to &quot;总和生育率：&quot;">​</a></h5><p>总和生育率（TFR）是指一个国家或地区的妇女在育龄期间（通常为 15 至 49 岁）平均生育的子女数量。为了维持人口的长期稳定，即实现人口更替水平，一般认为总和生育率需要达到约 2.1 。这是因为在考虑到婴儿死亡率、性别比例等因素后，平均每个妇女生育约 2.1 个孩子，才能确保下一代人口数量与上一代大致相当。</p><p>（我是这么理解的，一对夫妇2个人至少要生2个孩子，才能保持人口不下降，是这个道理么？）</p><hr><h5 id="皮格马利翁效应" tabindex="-1">皮格马利翁效应 <a class="header-anchor" href="#皮格马利翁效应" aria-label="Permalink to &quot;皮格马利翁效应&quot;">​</a></h5><p>“皮格马利翁效应是指如果你对某人赋予强烈的期待，并且以最真诚的态度对待，这份期待就会变成现实。”</p><p>（期望效应/罗森塔尔效应）</p><hr><h5 id="人体代谢酒精" tabindex="-1">人体代谢酒精 <a class="header-anchor" href="#人体代谢酒精" aria-label="Permalink to &quot;人体代谢酒精&quot;">​</a></h5><p>人体每小时可以代谢10～15克酒精，代谢过程为乙醇-&gt;乙醛-&gt;乙酸-&gt;二氧化碳+水+能量 其中乙醛是具有细胞毒性和基因毒性的，所以乙醇是一类致癌物质。</p><hr><h5 id="酒精度的计算" tabindex="-1">酒精度的计算 <a class="header-anchor" href="#酒精度的计算" aria-label="Permalink to &quot;酒精度的计算&quot;">​</a></h5><p>酒精度是怎么算的：比如13度的酒，表示的是每100ml酒中含有13ml酒精</p><hr><h5 id="长尾理论" tabindex="-1">长尾理论 <a class="header-anchor" href="#长尾理论" aria-label="Permalink to &quot;长尾理论&quot;">​</a></h5><p>长尾理论由克里斯・安德森提出，该理论认为，由于成本和效率的因素，过去人们只能关注重要的人或事（头部部分），如果用正态分布曲线来描绘这些人或事，人们只能关注曲线的 “头部”，而将处于曲线 “尾部”、需要更多的精力和成本才能关注到的大多数人或事忽略。但在互联网时代，由于关注的成本大大降低，人们有可能以很低的成本关注正态分布曲线的 “尾部”，关注 “尾部” 产生的总体效益甚至会超过 “头部”。</p><hr><h5 id="帕金森定律" tabindex="-1">帕金森定律 <a class="header-anchor" href="#帕金森定律" aria-label="Permalink to &quot;帕金森定律&quot;">​</a></h5>',18)),r(t,{src:"https://gitee.com/zackzhengxy/picGallery/raw/main/imgs/帕金森定律.png"}),a[1]||(a[1]=e("hr",null,null,-1)),a[2]||(a[2]=e("h5",{id:"奥卡姆剃刀原则",tabindex:"-1"},[h("奥卡姆剃刀原则 "),e("a",{class:"header-anchor",href:"#奥卡姆剃刀原则","aria-label":'Permalink to "奥卡姆剃刀原则"'},"​")],-1)),r(t,{src:"https://gitee.com/zackzhengxy/picGallery/raw/main/imgs/奥卡姆剃刀原则.png"}),a[3]||(a[3]=e("hr",null,null,-1))])}const k=l(d,[["render",p]]);export{x as __pageData,k as default};
