import{_ as a,c as s,o,j as t}from"./chunks/framework.DsljODgs.js";const m=JSON.parse('{"title":"书摘","description":"","frontmatter":{"title":"书摘","author":"Zack Zheng","date":"2025/08/18 12:32","categories":["宽松货币之王"],"tags":["克里斯托弗·伦纳德(美)","经济学"]},"headers":[],"relativePath":"books/socialSciences/宽松货币之王/读书笔记.md","filePath":"books/socialSciences/宽松货币之王/读书笔记.md","lastUpdated":1755770395000}'),c={name:"books/socialSciences/宽松货币之王/读书笔记.md"};function n(r,e,l,i,d,p){return o(),s("div",null,[...e[0]||(e[0]=[t("p",null,"“为了执行量化宽松政策，纽约联邦储备银行的交易员会打电话给摩根大通等一级交易商，提出从该行购买价值80亿美元的国债。摩根大通将把美国国债出售给美联储交易员。然后，美联储交易员会按下几个键，告诉摩根士丹利的银行家查看其准备金账户。瞧，美联储立即在准备金账户中凭空创造了80亿美元来完成购买。反过来，摩根士丹利可以用这笔钱在更广泛的市场上购买资产。这就是美联储创造货币的方式”",-1),t("hr",null,null,-1)])])}const u=a(c,[["render",n]]);export{m as __pageData,u as default};
