import{_ as e,c as s,o,f as t}from"./app.99655c42.js";const b=JSON.parse('{"title":"读后感","description":"","frontmatter":{"title":"读后感","author":"Zack Zheng","date":"2024/11/06 14:53 - 生死疲劳","tags":["社科"]},"headers":[],"relativePath":"books/socialSciences/生死疲劳/读后感.md","lastUpdated":1731294680000}'),n={name:"books/socialSciences/生死疲劳/读后感.md"},l=t("p",null,"曾经无产阶级被人欺辱，那时无产阶级欺辱别人，现在，法治社会，无产阶级仍遭不平等的待遇。 受害人是谁呢？我作为无产阶级，自然是感觉受到侵害 作为我的对立面，受害人自然是他 也许根本不是阶级的问题，问题是，一个人活或者活得好的基础，是要另一个人活的不好 世间就是这样么？你死我活，我死你活",-1),_=t("hr",null,null,-1),c=t("p",null,"书中王乐云待生产，被篮脸所救，用驴子驮去县医院。听我爸说，我出生时，是村里的一个人，用马车驮着我妈去的医院，我知道他家在哪里，但是此时，我却不记得他的名字。他二婚娶了一个智力有缺陷的老婆，女儿也是智力低于普通人的。冬天烧炉子因为一氧化碳中毒，他和他的妻子，连同他的老母亲，都毒死在了屋子里。",-1),a=t("hr",null,null,-1),i=t("p",null,"书中用小米粥养猪崽子，我十分怀疑。作为一个农村人，我虽然不是那个年代的人，应该不会有人拿小米粥喂猪吧。",-1),r=t("hr",null,null,-1),d=t("p",null,"连续读到了一半，第200页，过多的作者莫言代入和蓝解放、西门金龙因为黄互助感情引发的荒诞举动冗长且无聊了。",-1),h=t("hr",null,null,-1),u=t("p",null,"公猪拿镜子，梳鬃毛，过于玄幻了，这就是超现实主义么？",-1),p=[l,_,c,a,i,r,d,h,u];function f(m,k,$,g,x,B){return o(),s("div",null,p)}const v=e(n,[["render",f]]);export{b as __pageData,v as default};
