import{_ as n}from"./chunks/theme.BXb4sqay.js";import{_ as i,c as s,o as l,aG as r,G as o,j as e,a as p}from"./chunks/framework.CdnsPyyz.js";const x=JSON.parse('{"title":"金融学相关术语","description":"","frontmatter":{"title":"金融学相关术语","author":"Zack Zheng","date":"2025/04/27 11:20","categories":["金融学"],"tags":["经济学","金融学"]},"headers":[],"relativePath":"others/economy/金融学相关术语.md","filePath":"others/economy/金融学相关术语.md","lastUpdated":1746495469000}'),h={name:"others/economy/金融学相关术语.md"};function _(d,a,c,m,u,f){const t=n;return l(),s("div",null,[a[0]||(a[0]=r('<h4 id="转移净收入" tabindex="-1">转移净收入 <a class="header-anchor" href="#转移净收入" aria-label="Permalink to &quot;转移净收入&quot;">​</a></h4><p>转移净收入 = 转移性收入 - 转移性支出</p><p>转移性收入：比如离退休金、失业救济金、辞退金、保险索赔、住房公积金、养老金、子女给父母的赡养费等</p><p>转移性支出：缴纳税款、养老保险、医疗保险、赡养支出、捐赠支出、个人所得税、捐款</p><hr><h4 id="名义工资" tabindex="-1">名义工资 <a class="header-anchor" href="#名义工资" aria-label="Permalink to &quot;名义工资&quot;">​</a></h4><p>名义工资是指以货币数量表示的工资数额，也被称为货币工资。它是雇主在一定时期内支付给雇员的货币报酬，不考虑物价变动因素对货币购买力的影响。 （就是多少钱工资，名义工资上涨不一定意味这劳动者实际生活水平提高）</p><hr><h4 id="经常账户" tabindex="-1">经常账户 <a class="header-anchor" href="#经常账户" aria-label="Permalink to &quot;经常账户&quot;">​</a></h4>',9)),o(t,{src:"https://gitee.com/zackzhengxy/picGallery/raw/main/imgs/经常账户.png"}),a[1]||(a[1]=e("hr",null,null,-1)),a[2]||(a[2]=e("h4",{id:"债券",tabindex:"-1"},[p("债券 "),e("a",{class:"header-anchor",href:"#债券","aria-label":'Permalink to "债券"'},"​")],-1)),o(t,{src:"https://gitee.com/zackzhengxy/picGallery/raw/main/imgs/债券.png"}),a[3]||(a[3]=r('<hr><h4 id="通货膨胀" tabindex="-1">通货膨胀 <a class="header-anchor" href="#通货膨胀" aria-label="Permalink to &quot;通货膨胀&quot;">​</a></h4><p>是指在货币流通条件下，因货币供给大于货币实际需求，也即现实购买力大于产出供给，导致货币贬值，而引起的一段时间内物价持续而普遍地上涨现象。</p><p>实质是社会总需求大于社会总供给</p><p>(可以理解为大家都有钱了，都想买，但是东西不够了，价格就涨了)</p><h4 id="通货紧缩" tabindex="-1">通货紧缩 <a class="header-anchor" href="#通货紧缩" aria-label="Permalink to &quot;通货紧缩&quot;">​</a></h4><p>指的是市场上流通的货币量少于商品流通中所需要的货币量而引起的货币升值、物价普遍持续下跌的状况</p><p>质是社会总需求小于社会总供给</p><p>(可以理解为大家都没钱了，东西多了，价格就跌了)</p><h4 id="滞胀" tabindex="-1">滞胀 <a class="header-anchor" href="#滞胀" aria-label="Permalink to &quot;滞胀&quot;">​</a></h4><p>停滞性通货膨胀，是指经济停滞、失业及通货膨胀同时持续高涨的经济现象，也就是说在经济衰退或增长缓慢、失业率上升的同时，物价水平却持续上涨</p>',11))])}const P=i(h,[["render",_]]);export{x as __pageData,P as default};
