import{_ as e,c as a,o as p,a as t}from"./app.8effd384.js";const f=JSON.parse('{"title":"进制间的转换","description":"","frontmatter":{"title":"进制间的转换","author":"Zack Zheng","date":"2023/03/08 22:37","categories":["大海拾遗"],"tags":["进制转换"]},"headers":[{"level":3,"title":"十进制(整数)转为二进制","slug":"十进制-整数-转为二进制","link":"#十进制-整数-转为二进制","children":[]},{"level":3,"title":"十进制(小数)转为二进制","slug":"十进制-小数-转为二进制","link":"#十进制-小数-转为二进制","children":[]},{"level":3,"title":"二进制转为十进制","slug":"二进制转为十进制","link":"#二进制转为十进制","children":[]},{"level":3,"title":"二进制转十六进制","slug":"二进制转十六进制","link":"#二进制转十六进制","children":[]}],"relativePath":"program/pieces/2023/03/07/进制间的转换.md","lastUpdated":1678285548000}'),r={name:"program/pieces/2023/03/07/进制间的转换.md"},i=t('<h3 id="十进制-整数-转为二进制" tabindex="-1">十进制(整数)转为二进制 <a class="header-anchor" href="#十进制-整数-转为二进制" aria-hidden="true">#</a></h3><p><code>除2取余，逆序排列</code>（除2直到商为0）</p><p>如：255（十进制）=11111111（二进制）</p><p>255/2=127=====余1</p><p>127/2=63======余1</p><p>63/2=31=======余1</p><p>31/2=15=======余1</p><p>15/2=7========余1</p><p>7/2=3=========余1</p><p>3/2=1=========余1</p><p>1/2=0=========余1</p><h3 id="十进制-小数-转为二进制" tabindex="-1">十进制(小数)转为二进制 <a class="header-anchor" href="#十进制-小数-转为二进制" aria-hidden="true">#</a></h3><p><code>乘2取整，顺序排列</code>（乘2直到积为整数）</p><p>案例1：如 0.625=（0.101）</p><p>0.625*2=1.25======取出整数部分1</p><p>0.25*2=0.5========取出整数部分0</p><p>0.5*2=1==========取出整数部分1</p><h3 id="二进制转为十进制" tabindex="-1">二进制转为十进制 <a class="header-anchor" href="#二进制转为十进制" aria-hidden="true">#</a></h3><p>【例题】把二进制1.1101转化为十进制数。</p><p>解析：整数部分转化为十进制数是1=1，</p><p>小数部分1+1+0+1=0.8125，</p><p>则二进制数1.1101对应的十进制数是1.8125。</p><h3 id="二进制转十六进制" tabindex="-1">二进制转十六进制 <a class="header-anchor" href="#二进制转十六进制" aria-hidden="true">#</a></h3><p>四位二进制数看作一个整体，那么刚好与十六进制的一个数对应</p><p>案例1：（10111101010）= 0101 1110 1010 =5EA</p>',25),d=[i];function c(n,l,s,h,o,_){return p(),a("div",null,d)}const g=e(r,[["render",c]]);export{f as __pageData,g as default};
